listitem.screen:
public static void getUser( ResultSet [] rs )
                       throws SQLException
{
   Connection conn = DriverManager.getConnection("jdbc:default:connection");
   Statement stmt = conn.createStatement() ;
   rs[0] = stmt.executeQuery("SELECT USER FROM (VALUES(1)0 X(A);");
}

